import React, {Component} from "react";
import { Rating, Card, Image } from "semantic-ui-react";
import Complaints from "./Complaints";
import telefonica from '../Home/telefonica.png';
import { makeStyles } from '@material-ui/core/styles'
import {Grid, Button, Box} from '@material-ui/core';

import Header from "../Basic/Header";
import MaterialTable from "material-table";
import FilterListIcon from '@material-ui/icons/FilterList';
import SearchIcon from '@material-ui/icons/Search';
import Clear from '@material-ui/icons/Clear';
import FirstPageIcon from '@material-ui/icons/FirstPage';
import LastPageIcon from '@material-ui/icons/LastPage';
import ArrowBackIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIcon from '@material-ui/icons/ArrowForwardIos';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft'
import ChevronRightIcon from '@material-ui/icons/ChevronRight'
import ArrowDownward from '@material-ui/icons/ArrowDownward';
import CircularGraph from "../Graphics/CircularGraph";
import GraphicsLayout from "../Graphics/GraphicsLayout";

const useStyles = makeStyles((theme) => ({
    root: {
      backgroundColor: theme.palette.primary.main
    }
  }));


<link href="https://fonts.googleapis.com/icon?family=Material+Icons"
      rel="stylesheet">

      </link>

const columns=[
    { title: 'Avatar', field: 'imageUrl', render: rowData => <img src={rowData.imageUrl} style={{width: 40, borderRadius: '50%'}}/>,
    cellStyle: {
        backgroundColor: '#FAD7A0',
        color: '#A04000'
      },
      headerStyle: {
          backgroundColor: '#EB984E',
        }
     },
    { title: "Tipo de denuncia", field: "tipoDenuncia" ,
    cellStyle: {
      backgroundColor: '#FAD7A0',
      color: '#A04000'
    },
    headerStyle: {
        backgroundColor: '#EB984E',
      }
    },

    { title: "Fecha", field: "fecha" ,
    cellStyle: {
      backgroundColor: '#FAD7A0',
      color: '#A04000'
    },
    headerStyle: {
        backgroundColor: '#EB984E',
      }
    },

    { title: "Descripción", field: "desc",
    cellStyle: {
        backgroundColor: '#FAD7A0',
        color: '#A04000'
      },
      headerStyle: {
          backgroundColor: '#EB984E',
        }
      },

    {title: "Denunciada antes", field: "reported",  type: "bool",
    cellStyle: {
        backgroundColor: '#FAD7A0',
        color: '#A04000'
      },
      headerStyle: {
          backgroundColor: '#EB984E',
        }
      },
]

const data=[
    {
    tipoDenuncia: "Género",
    fecha: "1/1/2022",
    desc: "Esto será un link",
    reported: true,
    imageUrl: 'https://cdn.pixabay.com/photo/2015/06/20/07/24/color-815547_960_720.png'
    },

    {
    tipoDenuncia: "Otro",
    fecha: "3/1/2022",
    desc: "Esto será un link",
    reported: false,
    imageUrl: 'https://www.todofondos.net/wp-content/uploads/fondos-de-colores-fondos-de-pantalla.-fondo-de-pantalla-lisos.jpg'
    },
    {
    tipoDenuncia: "Raza",
    fecha: "1/2/2022",
    desc: "Esto será un link",
    reported: false,
    imageUrl: 'https://cdn.pixabay.com/photo/2015/06/20/07/24/color-815546_960_720.png'
    },
    {
    tipoDenuncia: "Política",
    fecha: "3/11/2021",
    desc: "Esto será un link",
    reported: true,
    imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRzhWS9iJ40wlMrZDIazgd8ilESJIB91r08wjN_FLrExTrtrzbUq8dyggaig3u_bobzuxI&usqp=CAU'
    },
    {
      tipoDenuncia: "Raza",
      fecha: "12/2/2022",
      desc: "Esto será un link",
      reported: true,
      imageUrl: 'https://cdn.pixabay.com/photo/2015/06/20/07/24/color-815546_960_720.png'
      },

]




function Company (){
        const theme = useStyles();
        return (


            <div className={theme.root}>
                <Grid container direction = "column">
                    <Grid item><Header/></Grid> 
                    <Grid item container>
                        <Grid item xs = {0} sm = {2}/>
                        <Grid item xs = {0} sm = {2}/>
                    </Grid>
                </Grid>
                <h3>Company</h3>
                <Image
                    centered
                    size='large'
                    src={telefonica}
                />
                <Rating/>
                <Rating/>
                <Rating/>
                <Rating/>
                <Rating/>
                <div className={theme.root}>
                  <Grid container justify = "center">
                      <MaterialTable

                          columns = {columns}
                          data = {data}
                          title = "Lista de denuncias reportadas:"
                          actions = {[
                              {
                              icon: 'favorite_border',
                              tooltip: 'Apoya esta denuncia', 
                              onClick: (event, rowData) => window.confirm("¿Deseas apoyar esta denuncia de tipo " + rowData.tipoDenuncia + "?")
                              },
                          ]}
                          components={{
                              Action: props => (
                                <Button
                                  onClick={(event) => props.action.onClick(event, props.data)}
                                  color="primary"
                                  variant="contained"
                                  style={{textTransform: 'none'}}
                                  size="small"
                                >
                                  Apoyar
                                </Button>
                              ),
                            }}
                          options ={{
                              actionsColumnIndex: -1,
                              headerStyle: {
                                  backgroundColor: '#FAD7A0',
                                  color: '#FFF'
                                },
                              sorting: true,
                              filtering: true,
                              search: true,
                          }}
                          localization = {{
                              header: {
                                  actions: "Apoyar"
                              }
                          }}
                          icons={{
                            Filter: () => <FilterListIcon style={{ color: "orange" }} />,
                            Search: () => <SearchIcon style={{ color: "blue" }} />,
                            ResetSearch: () => <Clear style={{ color: "blue" }} />,
                            FirstPage: () => <FirstPageIcon style={{ color: "blue" }} />,
                            LastPage: () => <LastPageIcon style={{ color: "blue" }} />,
                            ArrowBack: () => <ArrowBackIcon style={{ color: "blue" }} />,
                            ArrowForward: () => <ArrowForwardIcon style={{ color: "blue" }} />,
                            PreviousPage: () => <ChevronLeftIcon style={{ color: "blue" }} />,
                            NextPage: () => <ChevronRightIcon style={{ color: "blue" }} />,
                            SortArrow: () => <ArrowDownward style={{ color: "white" }}/>,
                          }}
                      />
                  </Grid> 
                  <Grid container direction = "column">

                      <Grid item><GraphicsLayout/></Grid> 
                      <Grid item><GraphicsLayout/></Grid> 
                      <Grid item><GraphicsLayout/></Grid> 
                  </Grid> 
                  <Grid container>
                    <Grid item xs={6}> 
                      <GraphicsLayout/> 
                    </Grid>
                    <Grid item xs={6}>
                      <GraphicsLayout/>
                    </Grid>
                  </Grid>

                  {/* <Grid container direction = "column">
                      <Grid item><CircularGraph/></Grid> 

                  </Grid>
            */}
              </div>  
            </div>


        )



}

export default Company;
