import React, {Component} from "react";
import { Rating, Card, Image } from "semantic-ui-react";
import telefonica from '../Home/telefonica.png';
import { makeStyles } from '@material-ui/core/styles'
import {Grid, Button, Box, Paper} from '@material-ui/core';
import CompanyTable from './CompanyTable'
import Header from "../Basic/Header";
import '../Graphics/graphics.css';
import SendIcon from '@material-ui/icons/Send';

import GraphicsLayout from "../Graphics/GraphicsLayout";


<link href="https://fonts.googleapis.com/icon?family=Material+Icons"
      rel="stylesheet">

      </link>



const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.primary.main
  },

  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 1000,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
}));


function Company (){
        const classes = useStyles();
        return (


            <div className={classes.root}>
              <Paper className={classes.paper}>
                <Grid container spacing={2} direction = "column">
                    <Grid item><Header/></Grid> 
                    <Grid item container>
                        <Grid item xs = {0} sm = {2}/>
                        <Grid item xs = {0} sm = {2}/>
                    </Grid>
                </Grid>
              </Paper>



              <Paper className={classes.paper}>
                <Grid container spacing={2} direction = "column">
                  <Image
                      centered
                      size='large'
                      src={telefonica}
                  />
                </Grid>



                <Grid container  spacing={2} justify = "center"  >
                  <Button variant="contained" endIcon={<SendIcon />} color="primary">
                    Denunciar caso de esta empresa
                  </Button>
                </Grid>   
              </Paper>




              <Paper className={classes.paper}>
                <Grid container spacing={2} justify = "center">
                    <CompanyTable/>
                </Grid> 



                <Grid container spacing={2} class = 'Box' >
                  <Grid item xs={6}> 
                    <GraphicsLayout/> 
                  </Grid>
                  <Grid item xs={6}>
                    <GraphicsLayout/>
                  </Grid>
                </Grid>


                <Grid container spacing={2}  direction = "column">
                    <Grid item><GraphicsLayout/></Grid> 
                </Grid> 
              </Paper>
            </div>


        )



}

export default Company;
