import React from 'react'
import { Button, Card, Image } from 'semantic-ui-react'
import telefonica from '../../Images/telefonica.png';
import { useHistory } from "react-router-dom";
const CompanyItem =({name,reputation,urlimg}) =>{
  let history = useHistory();

  const company = () => {
    history.push('/company')
  }
  return(
    <div class="ui raised card">
      <div class="content">
        <div class="header">Cute Dog</div>
        <div class="meta">
          <span class="category">Animals</span>
        </div>
        <div class="description">
          <p>En mi caso la experiencia fue horrible. Mi jefe constantemente me trataba como si fuese una persona inferior por el simple hecho de ser mujer.</p>
        </div>
      </div>
      <div class="extra content">
        <div class="right floated author">
          <img class="ui avatar image" src="/images/avatar/small/matt.jpg"/> Matt
        </div>
      </div>
    </div>

  )
}

export default CompanyItem;
